import{_ as t,o as e,c as r,d as o}from"./app.91d29e6e.js";var i="/post/assets/image-20230323134829047.6a7d9963.png";const u=JSON.parse('{"title":"\u5BF9\u79F0\u52A0\u5BC6\u7684\u6240\u7528\u7684\u5BC6\u94A5\u662F\u5982\u4F55\u83B7\u53D6\u7684\uFF1F","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u5BF9\u79F0\u52A0\u5BC6\u7684\u6240\u7528\u7684\u5BC6\u94A5\u662F\u5982\u4F55\u83B7\u53D6\u7684\uFF1F","slug":"\u5BF9\u79F0\u52A0\u5BC6\u7684\u6240\u7528\u7684\u5BC6\u94A5\u662F\u5982\u4F55\u83B7\u53D6\u7684\uFF1F"},{"level":3,"title":"\u9A8C\u8BC1\u6570\u5B57\u7B7E\u540D\u7684\u6709\u6548\u6027\u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u6B65\u9AA4\uFF1A","slug":"\u9A8C\u8BC1\u6570\u5B57\u7B7E\u540D\u7684\u6709\u6548\u6027\u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u6B65\u9AA4\uFF1A"}],"relativePath":"articles/\u6742/\u975E\u5BF9\u79F0\u5BC6\u94A5\u7684\u4F20\u8F93.md","lastUpdated":1704204110000}'),l={name:"articles/\u6742/\u975E\u5BF9\u79F0\u5BC6\u94A5\u7684\u4F20\u8F93.md"},s=o('<p>\u5BF9\u79F0\u52A0\u5BC6\u4E2D\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u4E00\u4E2A\u5BC6\u94A5\uFF0C\u901A\u4FE1\u53CC\u65B9\u540C\u65F6\u6301\u6709\u3002\u800C\u975E\u5BF9\u79F0\u52A0\u5BC6\u9700\u89814\u4E2A\u5BC6\u94A5\u3002\u901A\u4FE1\u53CC\u65B9\u5404\u81EA\u51C6\u5907\u4E00\u5BF9\u516C\u94A5\u548C\u79C1\u94A5\u3002\u5176\u4E2D\u516C\u94A5\u662F\u516C\u5F00\u7684\uFF0C\u7531\u4FE1\u606F\u63A5\u53D7\u65B9\u63D0\u4F9B\u7ED9\u4FE1\u606F\u53D1\u9001\u65B9\u3002\u516C\u94A5\u7528\u6765\u5BF9\u4FE1\u606F\u52A0\u5BC6\u3002\u79C1\u94A5\u7531\u4FE1\u606F\u63A5\u53D7\u65B9\u4FDD\u7559\uFF0C\u7528\u6765\u89E3\u5BC6\u3002\u65E2\u7136\u516C\u94A5\u662F\u516C\u5F00\u7684\uFF0C\u5C31\u4E0D\u5B58\u5728\u4FDD\u5BC6\u95EE\u9898\u3002\u4E5F\u5C31\u662F\u8BF4\u975E\u5BF9\u79F0\u52A0\u5BC6\u5B8C\u5168\u4E0D\u5B58\u5728\u5BC6\u94A5\u914D\u9001\u95EE\u9898\uFF01\u4F60\u770B\uFF0C\u662F\u4E0D\u662F\u5B8C\u7F8E\u89E3\u51B3\u4E86\u5BC6\u94A5\u914D\u9001\u95EE\u9898\uFF1F</p><p>\u56DE\u5230\u521A\u624D\u7684\u4F8B\u5B50\uFF0C\u5C0F\u660E\u548C\u4E0B\u7EA2\u7ECF\u8FC7\u7814\u7A76\u53D1\u73B0\u975E\u5BF9\u79F0\u52A0\u5BC6\u80FD\u89E3\u51B3\u4ED6\u4EEC\u901A\u4FE1\u7684\u5B89\u5168\u95EE\u9898\uFF0C\u4E8E\u662F\u505A\u4E86\u4E0B\u9762\u7684\u4E8B\u60C5\uFF1A</p><p>1\u3001\u5C0F\u660E\u786E\u5B9A\u4E86\u81EA\u5DF1\u7684\u79C1\u94A5 mPrivateKey\uFF0C\u516C\u94A5 mPublicKey\u3002\u81EA\u5DF1\u4FDD\u7559\u79C1\u94A5\uFF0C\u5C06\u516C\u94A5mPublicKey\u53D1\u7ED9\u4E86\u5C0F\u7EA2</p><p>2\u3001\u5C0F\u7EA2\u786E\u5B9A\u4E86\u81EA\u5DF1\u7684\u79C1\u94A5 hPrivateKey\uFF0C\u516C\u94A5 hPublicKey\u3002\u81EA\u5DF1\u4FDD\u7559\u79C1\u94A5\uFF0C\u5C06\u516C\u94A5 hPublicKey \u53D1\u7ED9\u4E86\u5C0F\u660E</p><p>3\u3001\u5C0F\u660E\u53D1\u9001\u4FE1\u606F \u201C\u5468\u516D\u65E910\u70B9soho T1\u697C\u4E0B\u89C1\u201D\uFF0C\u5E76\u4E14\u7528\u5C0F\u7EA2\u7684\u516C\u94A5 hPublicKey \u8FDB\u884C\u52A0\u5BC6\u3002</p><p>4\u3001\u5C0F\u7EA2\u6536\u5230\u4FE1\u606F\u540E\u7528\u81EA\u5DF1\u7684\u79C1\u94A5 hPrivateKey \u8FDB\u884C\u89E3\u5BC6\u3002\u7136\u540E\u56DE\u590D \u201C\u6536\u5230\uFF0C\u4E0D\u8981\u8FDF\u5230\u201D \u5E76\u7528\u5C0F\u660E\u7684\u516C\u94A5mPublicKey\u52A0\u5BC6\u3002</p><p>5\u3001\u5C0F\u660E\u6536\u5230\u4FE1\u606F\u540E\u7528\u81EA\u5DF1\u7684\u79C1\u94A5 mPrivateKey \u8FDB\u884C\u89E3\u5BC6\u3002\u8BFB\u53D6\u4FE1\u606F\u540E\u5FC3\u91CC\u6697\u60F3\uFF1A\u8FD8\u63D0\u9192\u6211\u4E0D\u8FDF\u5230\uFF1F\u6BCF\u6B21\u8FDF\u5230\u7684\u90FD\u662F\u4F60\u5427\uFF1F</p><p>\u4EE5\u4E0A\u8FC7\u7A0B\u662F\u4E00\u6B21\u5B8C\u6574\u7684request\u548Cresponse\u3002\u901A\u8FC7\u8FD9\u4E2A\u4F8B\u5B50\u6211\u4EEC\u68B3\u7406\u51FA\u4E00\u6B21\u4FE1\u606F\u4F20\u8F93\u7684\u975E\u5BF9\u79F0\u52A0\u3001\u89E3\u5BC6\u8FC7\u7A0B\uFF1A</p><p>1\u3001\u6D88\u606F\u63A5\u6536\u65B9\u51C6\u5907\u597D\u516C\u94A5\u548C\u79C1\u94A5</p><p>2\u3001\u79C1\u94A5\u63A5\u6536\u65B9\u81EA\u5DF1\u7559\u5B58\u3001\u516C\u94A5\u53D1\u5E03\u7ED9\u6D88\u606F\u53D1\u9001\u65B9</p><p>3\u3001\u6D88\u606F\u53D1\u9001\u65B9\u4F7F\u7528\u63A5\u6536\u65B9\u516C\u94A5\u5BF9\u6D88\u606F\u8FDB\u884C\u52A0\u5BC6</p><p>4\u3001\u6D88\u606F\u63A5\u6536\u65B9\u7528\u81EA\u5DF1\u7684\u79C1\u94A5\u5BF9\u6D88\u606F\u89E3\u5BC6</p><p>\u516C\u94A5\u53EA\u80FD\u7528\u505A\u6570\u636E\u52A0\u5BC6\u3002\u516C\u94A5\u52A0\u5BC6\u7684\u6570\u636E\uFF0C\u53EA\u80FD\u7528\u5BF9\u5E94\u7684\u79C1\u94A5\u624D\u80FD\u89E3\u5BC6\u3002\u8FD9\u662F\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u6838\u5FC3\u6982\u5FF5\u3002</p><ul><li><p><a href="https://zhuanlan.zhihu.com/p/436455172" target="_blank" rel="noopener noreferrer">https://zhuanlan.zhihu.com/p/436455172</a></p></li><li><p><strong>\u4F7F\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\u4F20\u8F93\u4E00\u4E2A\u5BF9\u79F0\u5BC6\u94A5K\uFF0C\u8BA9\u670D\u52A1\u5668\u548C\u5BA2\u6237\u7AEF\u90FD\u5F97\u77E5\u3002\u7136\u540E\u4E24\u8FB9\u90FD\u4F7F\u7528\u8FD9\u4E2A\u5BF9\u79F0\u5BC6\u94A5K\u6765\u52A0\u5BC6\u89E3\u5BC6\u6536\u53D1\u6570\u636E\u3002\u56E0\u4E3A\u4F20\u8F93\u5BC6\u94A5K\u662F\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\u65B9\u5F0F\uFF0C\u5F88\u96BE\u7834\u89E3\u6BD4\u8F83\u5B89\u5168\u3002\u800C\u5177\u4F53\u4F20\u8F93\u6570\u636E\u5219\u662F\u7528\u5BF9\u79F0\u52A0\u5BC6\u65B9\u5F0F</strong></p></li><li><p><a href="https://icodebook.com/posts/crypt-cert" target="_blank" rel="noopener noreferrer">https://icodebook.com/posts/crypt-cert</a></p></li><li><p><a href="https://github.com/febobo/web-interview/issues/134" target="_blank" rel="noopener noreferrer">https://github.com/febobo/web-interview/issues/134</a></p></li><li><p><a href="https://www.51cto.com/article/628890.html" target="_blank" rel="noopener noreferrer">https://www.51cto.com/article/628890.html</a></p></li></ul><p><img src="'+i+'" alt="image-20230323134829047"></p><p>\u670D\u52A1\u7AEF\u5C06\u6570\u5B57\u7B7E\u540D\u53D1\u7ED9\u6D4F\u89C8\u5668\uFF0C\u6D4F\u89C8\u5668\u5229\u7528\u7CFB\u7EDF\u5DF2\u7ECF\u5185\u7F6E\u7684\u516C\u94A5\u9A8C\u7B7E\uFF0C\u786E\u8BA4\u7B7E\u540D\u6CA1\u95EE\u9898\uFF0C\u7136\u540E\u5C31\u63D0\u53D6\u51FA\u6765\u6570\u5B57\u7B7E\u540D\u4E2D\u7684\u516C\u94A5\uFF0C\u5F00\u59CB\u534F\u5546\u5BF9\u79F0\u52A0\u5BC6\u7684\u79C1\u94A5\u4E86\uFF5E</p><h3 id="\u5BF9\u79F0\u52A0\u5BC6\u7684\u6240\u7528\u7684\u5BC6\u94A5\u662F\u5982\u4F55\u83B7\u53D6\u7684\uFF1F" tabindex="-1">\u5BF9\u79F0\u52A0\u5BC6\u7684\u6240\u7528\u7684\u5BC6\u94A5\u662F\u5982\u4F55\u83B7\u53D6\u7684\uFF1F <a class="header-anchor" href="#\u5BF9\u79F0\u52A0\u5BC6\u7684\u6240\u7528\u7684\u5BC6\u94A5\u662F\u5982\u4F55\u83B7\u53D6\u7684\uFF1F" aria-hidden="true">#</a></h3><p>\u5728 HTTPS \u4E2D\uFF0C\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\u5E76\u4E0D\u662F\u516C\u5F00\u7684\uFF0C\u800C\u662F\u901A\u8FC7\u4E00\u79CD\u79F0\u4E3A&quot;\u63E1\u624B&quot;\uFF08handshake\uFF09\u7684\u8FC7\u7A0B\u52A8\u6001\u751F\u6210\u7684\u3002\u5728\u63E1\u624B\u8FC7\u7A0B\u4E2D\uFF0C\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u4E4B\u95F4\u4F1A\u534F\u5546\u4E00\u4E2A\u5BF9\u79F0\u5BC6\u94A5\uFF0C\u8BE5\u5BC6\u94A5\u968F\u540E\u7528\u4E8E\u52A0\u5BC6\u548C\u89E3\u5BC6\u4F20\u8F93\u7684\u6570\u636E\u3002</p><p>\u4EE5\u4E0B\u662F\u7B80\u8981\u7684 HTTPS \u63E1\u624B\u8FC7\u7A0B\uFF1A</p><ol><li><strong>\u5BA2\u6237\u7AEF\u53D1\u9001\u8BF7\u6C42\uFF1A</strong> \u5BA2\u6237\u7AEF\u5411\u670D\u52A1\u5668\u53D1\u8D77\u8FDE\u63A5\u8BF7\u6C42\uFF0C\u5E76\u8BF7\u6C42\u5EFA\u7ACB\u4E00\u4E2A\u5B89\u5168\u7684\u8FDE\u63A5\u3002</li><li><strong>\u670D\u52A1\u5668\u54CD\u5E94\uFF1A</strong> \u670D\u52A1\u5668\u6536\u5230\u5BA2\u6237\u7AEF\u8BF7\u6C42\u540E\uFF0C\u8FD4\u56DE\u4E00\u4E2A\u5305\u542B\u6570\u5B57\u8BC1\u4E66\u7684\u54CD\u5E94\u3002</li><li><strong>\u5BA2\u6237\u7AEF\u9A8C\u8BC1\u8BC1\u4E66\uFF1A</strong> \u5BA2\u6237\u7AEF\u9A8C\u8BC1\u670D\u52A1\u5668\u63D0\u4F9B\u7684\u6570\u5B57\u8BC1\u4E66\u7684\u5408\u6CD5\u6027\u3002\u8FD9\u5305\u62EC\u68C0\u67E5\u8BC1\u4E66\u662F\u5426\u7531\u53D7\u4FE1\u4EFB\u7684\u8BC1\u4E66\u9881\u53D1\u673A\u6784\uFF08CA\uFF09\u7B7E\u53D1\uFF0C\u662F\u5426\u6CA1\u6709\u8FC7\u671F\u7B49\u3002</li><li><strong>\u751F\u6210\u5BF9\u79F0\u5BC6\u94A5\uFF1A</strong> \u5982\u679C\u6570\u5B57\u8BC1\u4E66\u9A8C\u8BC1\u6210\u529F\uFF0C\u5BA2\u6237\u7AEF\u4F1A\u751F\u6210\u4E00\u4E2A\u968F\u673A\u7684\u5BF9\u79F0\u5BC6\u94A5\uFF0C\u7528\u4E8E\u52A0\u5BC6\u540E\u7EED\u7684\u901A\u4FE1\u3002</li><li><strong>\u4F7F\u7528\u516C\u94A5\u52A0\u5BC6\u5BF9\u79F0\u5BC6\u94A5\uFF1A</strong> \u5BA2\u6237\u7AEF\u4F7F\u7528\u670D\u52A1\u5668\u7684\u516C\u94A5\uFF08\u4ECE\u670D\u52A1\u5668\u7684\u6570\u5B57\u8BC1\u4E66\u4E2D\u63D0\u53D6\uFF09\u52A0\u5BC6\u751F\u6210\u7684\u5BF9\u79F0\u5BC6\u94A5\uFF0C\u7136\u540E\u5C06\u52A0\u5BC6\u540E\u7684\u5BF9\u79F0\u5BC6\u94A5\u53D1\u9001\u7ED9\u670D\u52A1\u5668\u3002</li><li><strong>\u670D\u52A1\u5668\u4F7F\u7528\u79C1\u94A5\u89E3\u5BC6\uFF1A</strong> \u670D\u52A1\u5668\u4F7F\u7528\u81EA\u5DF1\u7684\u79C1\u94A5\u89E3\u5BC6\u5BA2\u6237\u7AEF\u53D1\u9001\u7684\u52A0\u5BC6\u5BF9\u79F0\u5BC6\u94A5\u3002</li><li><strong>\u5EFA\u7ACB\u5B89\u5168\u8FDE\u63A5\uFF1A</strong> \u53CC\u65B9\u73B0\u5728\u90FD\u62E5\u6709\u76F8\u540C\u7684\u5BF9\u79F0\u5BC6\u94A5\uFF0C\u8BE5\u5BC6\u94A5\u7528\u4E8E\u52A0\u5BC6\u548C\u89E3\u5BC6\u5728\u5B89\u5168\u8FDE\u63A5\u4E0A\u53D1\u9001\u7684\u6570\u636E\u3002</li></ol><p>\u6574\u4E2A\u63E1\u624B\u8FC7\u7A0B\u786E\u4FDD\u4E86\u901A\u4FE1\u53CC\u65B9\u80FD\u591F\u5B89\u5168\u5730\u534F\u5546\u51FA\u4E00\u4E2A\u7528\u4E8E\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\uFF0C\u800C\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u6D89\u53CA\u7684\u516C\u94A5\u548C\u79C1\u94A5\u4E3B\u8981\u7528\u4E8E\u6570\u5B57\u8BC1\u4E66\u7684\u9A8C\u8BC1\u548C\u5BF9\u79F0\u5BC6\u94A5\u7684\u5B89\u5168\u4F20\u9012\u3002</p><p><img src="https://s2.51cto.com/oss/202010/18/77a47b55ce37018d34477f51c4932417.jpg" alt="img"></p><ul><li>\u5BA2\u6237\u7AEF(\u6D4F\u89C8\u5668)\u5411\u670D\u52A1\u7AEF\u53D1\u51FA\u8BF7\u6C42\uFF0C\u670D\u52A1\u7AEF\u8FD4\u56DE\u8BC1\u4E66\u7ED9\u5BA2\u6237\u7AEF\u3002</li><li>\u5BA2\u6237\u7AEF\u62FF\u5230\u8BC1\u4E66\u540E\uFF0C\u628A\u8BC1\u4E66\u91CC\u7684\u7B7E\u540D\u4E0E\u53CA\u660E\u6587\u4FE1\u606F\u5206\u522B\u53D6\u51FA\u6765\uFF0C\u7136\u540E\u4F1A\u7528\u81EA\u8EAB\u643A\u5E26\u7684CA\u673A\u6784\u7684\u516C\u94A5\u53BB\u89E3\u5BC6\u7B7E\u540D\uFF0C\u5F97\u5230\u4FE1\u606F\u6458\u89811\uFF0C\u7136\u540E\u518D\u5BF9\u660E\u6587\u4FE1\u606F\u8FDB\u884CHASH\uFF0C\u5F97\u5230\u4E00\u4E2A\u4FE1\u606F\u6458\u89812\uFF0C\u5BF9\u6BD4\u4FE1\u606F\u6458\u89811 \u548C\u4FE1\u606F\u6458\u89812\uFF0C\u5982\u679C\u4E00\u6837\uFF0C\u8BF4\u660E\u8BC1\u4E66\u662F\u5408\u6CD5\u7684\uFF0C\u4E5F\u5C31\u662F\u8BC1\u4E66\u91CC\u7684\u516C\u94A5\u662F\u6B63\u786E\u7684\u3002</li></ul><h3 id="\u9A8C\u8BC1\u6570\u5B57\u7B7E\u540D\u7684\u6709\u6548\u6027\u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u6B65\u9AA4\uFF1A" tabindex="-1">\u9A8C\u8BC1\u6570\u5B57\u7B7E\u540D\u7684\u6709\u6548\u6027\u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u6B65\u9AA4\uFF1A <a class="header-anchor" href="#\u9A8C\u8BC1\u6570\u5B57\u7B7E\u540D\u7684\u6709\u6548\u6027\u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u6B65\u9AA4\uFF1A" aria-hidden="true">#</a></h3><p>\u63A5\u6536\u8005\u4F7F\u7528\u83B7\u5F97\u7684\u516C\u94A5\u5BF9\u63A5\u6536\u5230\u7684\u4FE1\u606F\u8FDB\u884C\u89E3\u5BC6\uFF0C\u7136\u540E\u6BD4\u5BF9\u89E3\u5BC6\u540E\u7684\u7ED3\u679C\u4E0E\u539F\u59CB\u4FE1\u606F\u8FDB\u884C\u6BD4\u5BF9\uFF0C\u5982\u679C\u4E00\u81F4\uFF0C\u5C31\u8BF4\u660E\u6570\u5B57\u7B7E\u540D\u6709\u6548\u3002</p><ol><li><strong>\u83B7\u53D6\u516C\u94A5\uFF1A</strong> \u83B7\u53D6\u6570\u5B57\u7B7E\u540D\u7684\u5BF9\u5E94\u516C\u94A5\u3002\u8FD9\u901A\u5E38\u6D89\u53CA\u5230\u4ECE\u8BC1\u4E66\u4E2D\u63D0\u53D6\u516C\u94A5\uFF0C\u6216\u8005\u4ECE\u5BC6\u94A5\u7BA1\u7406\u7CFB\u7EDF\u4E2D\u83B7\u53D6\u3002</li><li><strong>\u63D0\u53D6\u7B7E\u540D\u548C\u539F\u59CB\u6570\u636E\uFF1A</strong> \u4ECE\u6570\u5B57\u7B7E\u540D\u4E2D\u63D0\u53D6\u7B7E\u540D\u503C\u548C\u539F\u59CB\u6570\u636E\u3002</li><li><strong>\u4F7F\u7528\u516C\u94A5\u9A8C\u8BC1\u7B7E\u540D\uFF1A</strong> \u4F7F\u7528\u76F8\u5E94\u7684\u516C\u94A5\u5BF9\u539F\u59CB\u6570\u636E\u8FDB\u884C\u89E3\u5BC6\uFF0C\u5F97\u5230\u89E3\u5BC6\u540E\u7684\u6458\u8981\u3002</li><li><strong>\u751F\u6210\u539F\u59CB\u6570\u636E\u7684\u6458\u8981\uFF1A</strong> \u5BF9\u539F\u59CB\u6570\u636E\u8FDB\u884C\u76F8\u540C\u7684\u54C8\u5E0C\u7B97\u6CD5\uFF0C\u751F\u6210\u6458\u8981\u3002</li><li><strong>\u6BD4\u8F83\u6458\u8981\u503C\uFF1A</strong> \u6BD4\u8F83\u89E3\u5BC6\u540E\u7684\u6458\u8981\u548C\u751F\u6210\u7684\u6458\u8981\u3002\u5982\u679C\u4E24\u8005\u5339\u914D\uFF0C\u5219\u6570\u5B57\u7B7E\u540D\u6709\u6548\u3002</li></ol><p>\u8FD9\u4E2A\u8FC7\u7A0B\u5229\u7528\u4E86\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u539F\u7406\u3002\u6570\u5B57\u7B7E\u540D\u662F\u7531\u79C1\u94A5\u751F\u6210\u7684\uFF0C\u800C\u9A8C\u8BC1\u8FC7\u7A0B\u4F7F\u7528\u5BF9\u5E94\u7684\u516C\u94A5\u3002\u6570\u5B57\u7B7E\u540D\u7684\u89E3\u5BC6\u8FC7\u7A0B\u5B9E\u9645\u4E0A\u662F\u5BF9\u6BD4\u539F\u59CB\u6570\u636E\u7684\u54C8\u5E0C\u6458\u8981\u548C\u7B7E\u540D\u4E2D\u7684\u6458\u8981\uFF0C\u5982\u679C\u4E00\u81F4\uFF0C\u8BF4\u660E\u6570\u5B57\u7B7E\u540D\u6709\u6548\u3002</p>',27),p=[s];function a(n,c,h,g,_,d){return e(),r("div",null,p)}var b=t(l,[["render",a]]);export{u as __pageData,b as default};
